import React, { useState, useEffect, useRef } from 'react';
import { useParams } from 'react-router-dom';
import io from 'socket.io-client';
import {
  Box,
  Paper,
  TextField,
  Typography,
  Chip,
  Snackbar,
  Alert,
} from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';

const SOCKET_URL = 'http://localhost:5000';

function NoteRoom() {
  const { roomId } = useParams();
  const [note, setNote] = useState('');
  const [socket, setSocket] = useState(null);
  const [notification, setNotification] = useState({ open: false, message: '', severity: 'info' });
  const noteRef = useRef(null);

  useEffect(() => {
    const newSocket = io(SOCKET_URL);
    setSocket(newSocket);

    // Join room
    newSocket.emit('join-room', roomId);

    // Fetch initial note content
    fetch(`http://localhost:5000/api/notes/${roomId}`)
      .then(res => res.json())
      .then(data => setNote(data.content || ''))
      .catch(err => console.error('Error fetching note:', err));

    // Listen for note updates
    newSocket.on('note-updated', (data) => {
      setNote(data.content);
    });

    // Listen for user join notifications
    newSocket.on('user-joined', (data) => {
      setNotification({
        open: true,
        message: 'New user joined the room',
        severity: 'info'
      });
    });

    return () => {
      newSocket.close();
    };
  }, [roomId]);

  const handleNoteChange = (e) => {
    const newContent = e.target.value;
    setNote(newContent);

    if (socket) {
      socket.emit('note-update', {
        roomId,
        content: newContent
      });
    }
  };

  const copyRoomId = () => {
    navigator.clipboard.writeText(roomId);
    setNotification({
      open: true,
      message: 'Room ID copied to clipboard!',
      severity: 'success'
    });
  };

  return (
    <Box sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
      <Box sx={{ mb: 2, display: 'flex', alignItems: 'center', gap: 2 }}>
        <Typography variant="h5">Room: {roomId}</Typography>
        <Chip
          icon={<ContentCopyIcon />}
          label="Copy Room ID"
          onClick={copyRoomId}
          color="primary"
          variant="outlined"
        />
      </Box>

      <Paper
        elevation={3}
        sx={{
          p: 2,
          flex: 1,
          display: 'flex',
          flexDirection: 'column',
        }}
      >
        <TextField
          multiline
          fullWidth
          value={note}
          onChange={handleNoteChange}
          variant="outlined"
          placeholder="Start typing your note..."
          sx={{ flex: 1 }}
          inputRef={noteRef}
        />
      </Paper>

      <Snackbar
        open={notification.open}
        autoHideDuration={3000}
        onClose={() => setNotification({ ...notification, open: false })}
      >
        <Alert
          onClose={() => setNotification({ ...notification, open: false })}
          severity={notification.severity}
          sx={{ width: '100%' }}
        >
          {notification.message}
        </Alert>
      </Snackbar>
    </Box>
  );
}

export default NoteRoom;
